define([
    'jquery',
    'hammer.jquery',
    'moddef',
    'minicolors',
    'vendor/chroma',
    'canvas-draw',
    'physicsjs',
    'physicsjs/renderers/canvas',
    'physicsjs/behaviors/constant-acceleration',
    'physicsjs/behaviors/verlet-constraints',
    'physicsjs/behaviors/attractor',
    'physicsjs/behaviors/interactive',
    'physicsjs/bodies/circle',
    'raf'
], function(
    $,
    _hjq,
    M,
    _jqminicolors,
    chroma,
    Draw,

    Physics,
    _pjsc,
    _pjsca,
    _pjsvc,
    _pjsat,
    _pjsin,
    _pjscir,
    _raf
) {
    'use strict';

    var minuteLabsLogo = new Image();
    minuteLabsLogo.src = 'data:image/png;base64,';

    var vFactor = 200; // scale the velocity rendering

    var log = throttle(function( arg ){
        window.console.log( arg );
    }, 100);

    var colors = {
        'grey': 'rgb(220, 220, 220)'
        ,'greyLight': 'rgb(237, 237, 237)'
        ,'greyDark': 'rgb(200, 200, 200)'

        ,'deepGrey': 'rgb(67, 67, 67)'
        ,'deepGreyLight': 'rgb(98, 98, 98)'

        ,'blue': 'rgb(40, 136, 228)'
        ,'blueLight': 'rgb(91, 191, 243)'
        ,'blueDark': 'rgb(18, 84, 151)'

        ,'blueGlass': 'rgb(221, 249, 255)'

        ,'blueFire': '#626ead'

        ,'green': 'rgb(121, 229, 0)'
        ,'greenLight': 'rgb(125, 242, 129)'
        ,'greenDark': 'rgb(64, 128, 0)'

        ,'red': 'rgb(233, 63, 51)'
        ,'redLight': 'rgb(244, 183, 168)'
        ,'redDark': 'rgb(167, 42, 34)'

        ,'orange': 'rgb(239, 132, 51)'
        ,'orangeLight': 'rgb(247, 195, 138)'
        ,'orangeDark': 'rgb(159, 80, 31)'

        ,'yellow': 'rgb(228, 212, 44)'
        ,'yellowLight': 'rgb(242, 232, 110)'
        ,'yellowDark': 'rgb(139, 129, 23)'

    };

    function throttle( fn, delay, scope ){
        var to
            ,call = false
            ,args
            ,cb = function(){
                clearTimeout( to );
                if ( call ){
                    call = false;
                    to = setTimeout(cb, delay);
                    fn.apply(scope, args);
                } else {
                    to = false;
                }
            }
            ;

        scope = scope || null;

        return function(){
            call = true;
            args = arguments;
            if ( !to ){
                cb();
            }
        };
    }

    $.fn.slider = function( opts ){
        var startevent = window.Modernizr.touch ? 'touchstart' : 'mousedown';
        var moveevent = window.Modernizr.touch ? 'touchmove' : 'mousemove';
        var endevent = window.Modernizr.touch ? 'touchend' : 'mouseup';
        var options = $.extend({
            min: 0
            ,max: 1
            ,value: 0.5
        }, opts);

        return $(this).each(function(){
            var $this = $(this).addClass('slider')
                ,factor = options.max - options.min
                ,val = (options.value - options.min) / factor
                ,$handle = $('<div>').appendTo($this).addClass('handle')
                ,$meter = $('<div>').appendTo($this).addClass('meter')
                ;

            function set( x ){
                var width = $this.width();
                if ( x !== undefined ){
                    x = Math.max(0, Math.min(width, x));
                    val = x / width;
                } else {
                    x = val * width;
                }

                $handle.css('left', x);
                $meter.css('width', (val * 100) + '%');

                $this.trigger('change', val * factor + options.min);
            }

            $this.css({
                position: this.style.position || 'relative'
            });

            $meter.css({
                display: 'block'
                ,position: 'absolute'
                ,top: '0'
                ,left: '0'
                ,bottom: '0'
            });

            $handle.css({
                position: 'absolute'
                ,top: '50%'
                ,marginLeft: -$handle.outerWidth() * 0.5
                ,marginTop: -$handle.outerHeight() * 0.5
            });

            var dragging = false;
            var drag = throttle(function( e ){

                if ( dragging ){

                    e.preventDefault();

                    if ( e.originalEvent.targetTouches ){
                        e = e.originalEvent.targetTouches[0];
                    }

                    var offset = $this.offset()
                        ,x = e.pageX - offset.left
                        ,y = e.pageY - offset.top
                        ;

                    set( x );
                }

            }, 20);

            $this.on(startevent, function( e ){
                dragging = true;
                drag( e );
            });
            $this.on(moveevent, drag);
            $this.on(endevent, function(){
                dragging = false;
            });

            $this.on('mousedown', function(){
                return false;
            });

            $this.on('refresh', function( e, v ){
                val = (v - options.min) / factor;
                set();
            });

            set( val * $this.width() );
        });
    };

    var pendulumStyles = {
            lineWidth: 3
            ,strokeStyle: colors.greyDark
            ,fillStyle: colors.greyDark
            ,shadowBlur: 1
        }
        ,vectorStyles = {
            strokeStyle: colors.blueDark
            ,fillStyle: colors.blueDark
            ,lineWidth: 2
        }
        ,pathStyles = {
            lineWidth: 3
            ,lineCap: 'butt'
            // ,shadowBlur: 4
        }
        ,selectedStyles = {
            lineWidth: 3
            ,strokeStyle: colors.red
            ,fillStyle: colors.red
        }
        ,selectedOutline = {
            lineWidth: 1
            ,strokeStyle: colors.greyLight
        }
        ,defaultPathColor = '#b9770b'
        ;

    function lerp(a, b, p) {
        return (b-a)*p + a;
    }

    Physics.behavior('position-tracker', function( parent ){

        return {
            connect: function( world ){

                world.on('integrate:positions', this.behave, this, -100);
            },
            disconnect: function( world ){
                world.off('integrate:positions', this.behave);
            },
            clear: function(){
                var bodies = this.getTargets()
                    ,body
                    ;
                for ( var i = 0, l = bodies.length; i < l; ++i ){

                    body = bodies[ i ];
                    body.positionBuffer = [];
                }
            },
            behave: function(){
                var bodies = this.getTargets()
                    ,body
                    ,list
                    ;

                for ( var i = 0, l = bodies.length; i < l; ++i ){

                    body = bodies[ i ];
                    list = body.positionBuffer || (body.positionBuffer = []);
                    if ( list.length > 100 ){
                        list.splice( 0, list.length - 100 );
                        list.push(body.state.old.pos.x, body.state.old.pos.y);
                    } else {
                        list.push(body.state.old.pos.x, body.state.old.pos.y);
                    }
                }
            }
        };
    });

    Physics.body.mixin({
        'color': function( hex ){
            if ( !hex ){
                return this._color;
            }

            this._color = chroma(hex).hex();
            this.lastColor = this._color;
            this.colorScale = getColorScale( this );
        }
        ,'velocityArrowHead': function( ret ){

            ret = ret || Physics.vector();
            return ret.clone( this.state.vel )
                .mult( vFactor )
                .vadd( this.state.pos )
                ;
        }
    });

    function getColorScale( body ){
        var c = chroma( body._color );

        return function( v ){
            v /= (0.8 * body.maxSpeed);
            v = Math.min(1, Math.max( 0 , v ));
            // v = (Math.exp( v ) - 1)/(Math.E - 1);
            var val = lerp(1, 0.1, v);
            return c.alpha( val ).css();
        }
    }

    function randomDir( v ){
        return v.set( 1, 0 ).rotate( Math.random() * Math.PI * 2 );
    }

    var Pendulum = function( world, x, y, g ){
        this.init( world, x, y, g );
    };

    Pendulum.prototype = {
        init: function( world, x, y, g ){
            this.center = Physics.body('circle', {
                x: x
                ,y: y
                ,treatment: 'static'
                ,radius: 5
                ,styles: $.extend({}, pendulumStyles, { fillStyle: colors.blueDark, strokeStyle: colors.blueDark })
            });

            this.g = g;

            this.colors = [ '#fff', colors.blueFire, colors.red, colors.yellow, colors.green, colors.grey, colors.blue ];

            this.view = world.renderer().createView( this.center.geometry, pendulumStyles );

            this.constraints = Physics.behavior('verlet-constraints', {
                iterations: 2
            });

            world.add( this.center ).add( this.constraints );
            this.world = world;
            this.bodies = [ this.center ];
        }
        ,addVertex: function( x, y ){

            var l = this.bodies.length;
            var v = { x: x, y: y, vel: { x: 0, y: 0 } };
            var b = Physics.body('circle', {
                x: x
                ,y: y
                ,radius: 15
                ,view: this.view
                ,initial: v
                ,path: (l !== 1)
                ,maxSpeed: 1
            });

            b.color( this.colors[ l - 1 ] || defaultPathColor );
            this.bodies.push( b );
            this.world.add( b );
            this.constraints.distanceConstraint( this.bodies[ l - 1 ], b, 1 );

            return b;
        }
        ,removeVertex: function(){
            var self = this;
            if ( this.bodies.length > 1 ){
                var b = this.bodies.pop();
                this.world.remove( b );
                var constrs = Physics.util.filter(this.constraints.getConstraints().distanceConstraints, Physics.query({
                    $or: [
                        { bodyA: b }
                        ,{ bodyB: b }
                    ]
                }));
                $.each(constrs, function( i, c ){
                    self.constraints.remove( c );
                });
            }
        }
        ,resetConstraints: function(){
            var v, b;
            this.constraints.drop();
            for ( var i = 1, l = this.bodies.length; i < l; i++ ){
                this.constraints.distanceConstraint( this.bodies[ i - 1 ], this.bodies[ i ], 1 );
            }
            return this;
        }
        ,reset: function(){
            var v, b, E = this.calcEnergy(), last = this.center, h = 0;
            for ( var i = 1, l = this.bodies.length; i < l; i++ ){
                b = this.bodies[ i ];
                v = b.initial;
                b.state.pos.clone( v );
                b.state.old.pos.clone( v );
                b.state.vel.clone( v.vel );
                // set the max anticipated speed based on energy calculation
                h += last.state.pos.dist( b.state.pos );
                b.maxSpeed = Math.sqrt(2 * E / b.mass);
                last = b;
            }
            return this;
        }
        ,calcEnergy: function(){
            var E = 0, last = this.center, v, b, h = 0, g = this.g;
            for ( var i = 1, l = this.bodies.length; i < l; i++ ){
                b = this.bodies[ i ];
                v = b.initial;
                h += last.state.pos.dist( b.state.pos );
                E += b.mass * (0.5 * b.state.vel.normSq() + g * (h - (b.state.pos.y - this.center.state.pos.y)));
                last = b;
            }

            return E;
        },
        randomize: function( maxLen ){
            maxLen = maxLen || 100;
            var n = ((4 * Math.random()) + 2) | 0;
            var dir = Physics.vector( 1, 0 );
            var last = this.center;
            var i;
            // clear
            while ( this.bodies.length > 1 ){
                this.removeVertex();
            }

            for ( i = 0; i < n; i++ ){
                randomDir( dir ).mult( Math.random() * maxLen + 20 ).vadd( last.state.pos );
                last = this.addVertex( dir.x, dir.y );
                randomDir( dir ).mult( Math.random() * 0.5 );
                last.mass = Math.random() * 10;
                last.initial.vel.x = dir.x;
                last.initial.vel.y = dir.y;
                last.state.vel.clone( dir );
            }
        },

        hash: function( hash ){
            var data, i, l, b;
            if ( hash === undefined ){
                data = [];
                for ( i = 1, l = this.bodies.length; i < l; i++ ){
                    b = this.bodies[ i ];
                    data.push({
                        x: b.initial.x
                        ,y: b.initial.y
                        ,vx: b.initial.vel.x
                        ,vy: b.initial.vel.y
                        ,mass: b.mass
                        ,color: b.color()
                        ,path: b.path
                    });
                }
                return '#pendulum=' + window.encodeURIComponent(window.btoa(JSON.stringify( data )));
            }

            // find settings
            hash = hash.match(/pendulum=([^&]*)/);

            if ( !hash || !hash.length ){
                return false;
            }

            // decode
            try {
                hash = window.atob(window.decodeURIComponent(hash[1]));
                data = $.parseJSON(hash);
            } catch( e ){
                return false;
            }

            if ( !data ){
                return false;
            }

            // set pendulum state
            while ( this.bodies.length > 1 ){
                this.removeVertex();
            }

            for ( i = 0, l = data.length; i < l; i++ ){
                b = this.addVertex( data[i].x, data[i].y );
                b.initial.vel.x = data[i].vx;
                b.initial.vel.y = data[i].vy;
                b.mass = data[i].mass;
                b.color( data[i].color );
                b.path = data[i].path;
            }

            return true;
        }
    };

    // Page-level Mediator
    var Mediator = M({

        // Mediator Constructor
        constructor: function(){

            var self = this;
            self.edit = false;
            self.initEvents();

            $(function(){
                self.onDomReady();
                self.resolve('domready');
            });
        }

        // Initialize events
        ,initEvents: function(){

            var self = this;

            window.addEventListener('resize', function(){
                self.width = window.innerWidth;
                self.height = window.innerHeight;
                self.emit('resize');
            }, true);

            self.on({
                pause: function(){
                    $('#controls .ctrl-pause')
                        .addClass('paused')
                        .html('Unpause')
                        ;
                }
                ,unpause: function(){
                    $('#controls .ctrl-pause')
                        .removeClass('paused')
                        .html('Pause')
                        ;
                }
            })

            $(function(){
                var ctrls = $('#controls')

                ctrls.hammer()
                    .on('touch', '.ctrl-download', function( e ){
                        var img = self.getImage();
                        this.href = img;
                        this.download = 'minutelabs-chaotic-pendulum.png';
                    })
                    .on('touch', '.ctrl-pause', function( e ){
                        e.preventDefault();
                        var $this = $(this)
                            ,paused = $this.hasClass('paused')
                            ;

                        self.emit(paused ? 'unpause' : 'pause');
                    })
                    .on('touch', '.ctrl-edit', function( e ){
                        e.preventDefault();
                        var $this = $(this);
                        self.edit = !self.edit;

                        ctrls.toggleClass('edit', self.edit);

                        $this
                            .html( self.edit ? 'Start' : 'Edit' )
                            .toggleClass('ok', self.edit)
                            .toggleClass('pop', !self.edit)
                            ;

                        self.emit(self.edit ? 'edit' : 'start');
                    })
                    .on('touch', '.ctrl-remove', function( e ){
                        e.preventDefault();
                        self.emit('remove');
                    })
                    .on('touch', '.ctrl-randomize', function( e ){
                        e.preventDefault();
                        self.emit('randomize');
                    })
                    .on('touch', '.ctrl-edit-velocities', function( e ){
                        e.preventDefault();
                        var on = ctrls.toggleClass('state-edit-velocities').is('.state-edit-velocities');
                        $(this).html( on ? 'Edit Initial Positions' : 'Edit Initial Velocities' );
                        self.editVelocities = on;
                        self.contextualMenu( null );
                    })
                    ;

                var body;

                $('#viewport').hammer()
                    .on('touchstart', function( e ){
                        e.preventDefault();
                    })
                    .on('touch', 'canvas', function( e ){
                        var pos = e.gesture.center;
                        pos = { x: pos.pageX - self.width / 2, y: pos.pageY - self.height/2 };
                        e.preventDefault();
                        if ( self.edit ){

                            body = self.world.findOne({ $at: pos });

                            if ( !body ){
                                pos = Physics.vector( pos );
                                body = self.world.findOne(function( body ){
                                    if ( body.velocityArrowHead().dist( pos ) < 10 ){
                                        return true;
                                    }
                                });
                                if ( body ){
                                    self.emit('grab-velocity', body);
                                    body = false;
                                    return;
                                }
                            }

                            if ( !body ) {

                                self.emit( 'create', pos );
                            }
                        }
                    })
                    .on('release', 'canvas', function( e ){
                        var pos = e.gesture.center;
                        pos = { x: pos.pageX - self.width / 2, y: pos.pageY - self.height/2 };
                        e.preventDefault();
                        if ( self.edit ){

                            body = self.world.findOne({ $at: pos });
                            self.emit('select', body);
                        }
                        body = false;
                        self.emit('release', e.gesture);
                    })
                    .on('dragstart', 'canvas', function( e ){
                        self.emit('grab', body);
                    })
                    .on('drag', 'canvas', function( e ){
                        e.preventDefault();
                        self.emit('drag', e.gesture);
                    })
                    ;
            });
        }

        ,getImage: function(){
            var layers = ['paths']
                ,el = this.renderer.layer('paths').hdel
                ,ctx = this.renderer.hiddenCtx
                ,canvas = this.renderer.hiddenCanvas
                ,opacity
                ,w = el.width
                ,h = el.height
                ;

            canvas.width = w;
            canvas.height = h;
            ctx.fillStyle = colors.deepGreyDark;
            ctx.fillRect( 0, 0, w, h );
            ctx.drawImage( el, 0, 0 );
            ctx.drawImage( minuteLabsLogo, 0, h - 96 );
            Draw( ctx )
                .styles({
                    fillStyle: colors.grey,
                    font: '40px "latin-modern-mono-light", Courier, monospace'
                })
                ;
            ctx.fillText('MinuteLabs.io', 96 + 30, h - 96/2 + 10 )

            // for ( var i = 0, l = layers.length; i < l; ++i ){
            //
            //     el = this.renderer.layer( layers[i] ).el;
            //     opacity = parseFloat(el.style.opacity, 10);
            //     ctx.globalAlpha = isNaN(opacity) ? 1 : opacity;
            //     ctx.drawImage(el, 0, 0);
            // }
            ctx.globalAlpha = 1;
            return canvas.toDataURL('image/png');
        }

        ,initPhysics: function( world ){

            var self = this
                ,viewWidth = self.width
                ,viewHeight = self.height
                // bounds of the window
                ,viewportBounds = Physics.aabb(0, 0, viewWidth, viewHeight)
                ,center = Physics.vector( viewWidth, viewHeight ).mult( 0.5 )
                ,renderer
                ,tracker = Physics.behavior('position-tracker')
                ,startT = Date.now()
                ,g = 0.0004
                ;

            // create a renderer
            self.renderer = renderer = Physics.renderer('canvas', {
                el: 'physics'
                ,width: viewWidth
                ,height: viewHeight
                ,offset: center
                // ,meta:true
            });

            // add the renderer
            world.add(renderer);

            // pendulum
            var pendulum = self.pendulum = new Pendulum( world, 0, 0, g );

            self.selectedView = renderer.createView( pendulum.center.geometry, selectedStyles );

            self.on({
                resize: function() {

                    var img = renderer.layer('paths').ctx.getImageData(0, 0, viewWidth, viewHeight);
                    var dx = (self.width - viewWidth)/2;
                    var dy = (self.height - viewHeight)/2;

                    viewWidth = self.width;
                    viewHeight = self.height;

                    renderer.resize( viewWidth, viewHeight );

                    renderer.layer('paths').ctx.putImageData( img, dx, dy );

                    center = Physics.vector( viewWidth, viewHeight ).mult( 0.5 );
                    renderer.layer('main').options.offset = center;
                }
                ,randomize: function( e ){
                    pendulum.randomize( viewHeight / 6 );
                    pendulum.reset();
                    Draw.clear( renderer.layer('paths').ctx );
                    Draw.clear( renderer.layer('paths').hdctx );
                    tracker.applyTo( pendulum.bodies );
                    self.emit('modified');
                }
                ,create: function( e, pos ){
                    if ( !self.selectedBody && !self.editVelocities ){
                        var p = pendulum.addVertex( pos.x, pos.y );
                        tracker.applyTo( pendulum.bodies );
                        self.emit('grab', p);
                    }
                }
                ,grab: function( e, body ){
                    var drag, orig, vis;

                    if ( body && body.initial ){
                        if ( self.editVelocities ){

                            self.emit('grab-velocity', body);

                        } else {
                            vis = self.$ctxMenu.is(':visible');
                            orig = body.state.pos.clone();
                            drag = function( e, g ){
                                body.state.pos.set( g.deltaX, g.deltaY ).vadd( orig );
                                body.initial.x = body.state.pos.x;
                                body.initial.y = body.state.pos.y;
                                pendulum.resetConstraints();
                            };

                            self.on('drag', drag);
                            self.on('release', function( e ){
                                self.off(e.topic, e.handler);
                                self.off('drag', drag);
                                if ( vis ){
                                    self.contextualMenu( body );
                                }
                                self.emit('modified');
                            });
                            self.$ctxMenu.hide();
                        }
                    }
                }
                ,'grab-velocity': function( e, body ){
                    var drag, orig, vis = self.$ctxMenu.is(':visible');
                    if ( body && body.initial ){
                        drag = function( e, g ){
                            body.state.vel
                                .set( g.center.pageX, g.center.pageY )
                                .vsub( center )
                                .vsub( body.state.pos )
                                .mult( 1/vFactor )
                                ;

                            body.initial.vel.x = body.state.vel.x;
                            body.initial.vel.y = body.state.vel.y;
                        };

                        self.on('drag', drag);
                        self.on('release', function( e ){
                            self.off(e.topic, e.handler);
                            self.off('drag', drag);
                            if ( vis ){
                                self.contextualMenu( body );
                            }
                            self.emit('modified');
                        });
                        self.$ctxMenu.hide();
                    }
                }
                ,select: function( e, body ){
                    if ( body && body.initial ){
                        self.contextualMenu( body );
                    } else {
                        self.contextualMenu( null );
                    }
                }
                ,edit: function(){
                    self.emit('pause');
                    setTimeout(function(){
                        world._meta.interpolateTime = 0;
                        pendulum.reset();
                        tracker.clear();
                        Draw.clear( renderer.layer('paths').ctx );
                        Draw.clear( renderer.layer('paths').hdctx );
                        self.contextualMenu( pendulum.bodies[ pendulum.bodies.length - 1 ] );
                        world.render();
                    }, 100);
                }
                ,start: function(){
                    self.contextualMenu( null );
                    pendulum.reset();
                    self.emit('unpause');
                }
                ,pause: function(){
                    world.pause();
                }
                ,unpause: function(){
                    world.unpause();
                }
                ,remove: function(){
                    self.contextualMenu( null );
                    pendulum.removeVertex();
                }
                ,modified: function(){
                    window.location.hash = pendulum.hash();
                }
            });

            if ( !pendulum.hash( window.location.hash ) ){
                var first = pendulum.addVertex( 140, 0 )
                first.mass = 10;
                first.state.vel.set( 0, 0.15 );
                first.initial.vel.y = 0.15;
                first.path = false;
                pendulum.addVertex( 240, 0 );
            }

            pendulum.reset();
            tracker.applyTo( pendulum.bodies );

            function len( x, y, x2, y2 ){
                x -= x2;
                y -= y2;
                return Math.sqrt( x*x + y*y );
            }

            renderer.layer('main').options.zIndex = 2;
            renderer.layer('main').options.offset = center;
            var oldrender = renderer.layer('main').render;
            renderer.layer('main').render = function(){
                var b, p, points = [];
                var t = world._meta.interpolateTime || 0;
                for ( var i = 0, l = pendulum.bodies.length; i < l; i++ ){
                    b = pendulum.bodies[i];
                    points.push([ b.state.pos.x + b.state.vel.x * t , b.state.pos.y + b.state.vel.y * t ]);
                }

                Draw( this.ctx )
                    .offset( center.x, center.y )
                    .clear()
                    .styles( pendulumStyles )
                    .lines( points )
                    ;

                oldrender.call(this, false);

                if ( self.selectedBody ){
                    b = self.selectedBody;
                    this.ctx.setLineDash( [3.14] );
                    Draw( this.ctx )
                        .styles( selectedOutline )
                        .circle( b.state.pos.x, b.state.pos.y, b.geometry.radius )
                        ;
                    this.ctx.setLineDash( [] );
                }
            };

            var pathRenderer = renderer.addLayer('paths', null, { zIndex: 1, offset: center });
            pathRenderer.hdel = document.createElement('canvas');
            pathRenderer.hdel.width = viewWidth * 2;
            pathRenderer.hdel.height = viewHeight * 2;
            pathRenderer.hdctx = pathRenderer.hdel.getContext('2d');
            pathRenderer.render = function(){
                var b, p, grad, c, oldc, j, ll, path = [];

                Draw( this.ctx )
                    .offset( center.x, center.y )
                    ;

                this.ctx.globalCompositeOperation = 'color-dodge';
                this.hdctx.globalCompositeOperation = 'color-dodge';
                this.hdctx.save();
                this.hdctx.translate( this.hdel.width/4 - center.x, this.hdel.height/4 - center.y );
                this.hdctx.scale( 2, 2 );

                for ( var i = 0, l = pendulum.bodies.length; i < l; i++ ){
                    b = pendulum.bodies[i];
                    p = b.positionBuffer;
                    if (!p){ continue; }

                    ll = p.length;
                    if ( b.path && ll >= 4 ){

                        oldc = b.oldColor;
                        c = b.colorScale( b.state.vel.norm() );
                        grad = this.ctx.createLinearGradient( p[0], p[1], p[ll-2], p[ll-1] );
                        grad.addColorStop( 0, oldc || c );
                        grad.addColorStop( 1, c );
                        pathStyles.strokeStyle = grad || c;
                        // pathStyles.shadowColor = c;

                        Draw( this.ctx )
                            .styles( pathStyles )
                            ;

                        Draw( this.hdctx )
                            .styles( pathStyles )
                            ;

                        b.oldColor = c;

                        Draw( this.ctx )
                            .lines( p )
                            ;

                        // draw to HD canvas
                        Draw( this.hdctx )
                            .lines( p )
                            ;
                    }

                    if ( p[ ll-1 ] ){
                        p[0] = p[ll-2];
                        p[1] = p[ll-1];
                        p.length = 2;
                    } else {
                        p.length = 0;
                    }
                }

                this.hdctx.restore();
            };

            renderer.addLayer('vectors', null, { zIndex: 1, offset: center }).render = function(){
                var b
                    ,p
                    ,scratch = Physics.scratchpad()
                    ,v = scratch.vector()
                    ,int = scratch.vector()
                    ,t = renderer.interpolateTime || 0
                    ,ang
                    ;

                Draw( this.ctx ).offset( 0, 0 ).styles( vectorStyles ).clear();

                for ( var i = 0, l = pendulum.bodies.length; i < l; i++ ){
                    b = pendulum.bodies[i];
                    v.clone( b.state.vel )
                        .mult( vFactor )
                        .vadd( int.clone(b.state.vel).mult(t).vadd(b.state.pos) )
                        .vadd( center )
                        ;

                    Draw
                        .line( center.x + int.x, center.y + int.y, v.x, v.y )
                        ;

                    ang = b.state.vel.angle();
                    this.ctx.translate( v.x, v.y );
                    this.ctx.rotate( ang );
                    Draw.arrowHead( 'right', 0, 0, 5 ).fill();
                    this.ctx.rotate( -ang );
                    this.ctx.translate( -v.x, -v.y );
                }

                scratch.done();
            };

            // add some fun interaction
            var attractor = Physics.behavior('attractor', {
                order: 0,
                strength: 0.002
            });

            // add things to the world
            world.add([
                Physics.behavior('constant-acceleration', { strength: g })
                ,tracker
            ]);

            // subscribe to ticker to advance the simulation
            Physics.util.ticker.on(function( time ) {
                world.step( time );
                world.render();
            });

            // start the ticker
            Physics.util.ticker.start();
        }

        ,contextualMenu: function( body ){

            var self = this
                ,el = self.$ctxMenu || (self.$ctxMenu = $('#ctx-menu'))
                ,oldBody = el.data('body')
                ;

            if ( oldBody ){
                oldBody.view = oldBody.oldView || oldBody.view;
            }

            if ( !body ){
                el.data('body', null).hide();
                self.selectedBody = null;
                return;
            }

            var x = body.state.pos.x + self.width / 2 + 10
                ,y = body.state.pos.y + self.height / 2 + 20
                ;

            if ( x > (self.width - el.outerWidth()) ){
                x -= (x + el.outerWidth() - self.width + 20);
            }
            if ( y > (self.height - el.outerHeight()) ){
                y -= el.outerHeight() + 20;
            }

            body.oldView = body.view;
            body.view = self.selectedView;
            self.selectedBody = body;

            el.data('body', body).show().css({
                top: y
                ,left: x
            });

            el.find('#ctrl-mass').trigger('refresh', body.mass);
            el.find('#ctrl-color').minicolors( 'value', body.color() );
            el.find('#ctrl-path').toggleClass( 'on', body.path );
        }

        // DomReady Callback
        ,onDomReady: function(){

            var self = this;
            self.width = window.innerWidth;
            self.height = window.innerHeight;
            self.$ctxMenu = $('#ctx-menu');
            var massLabel;
            $('#ctrl-mass').slider({ min: 0.1, max: 100, val: 1 }).on('change', function( e, val ){
                var b = self.$ctxMenu.data('body');
                massLabel.attr( 'data-val', val.toFixed(2) );
                if ( b ){
                    b.mass = val;
                }
                self.emit('modified');
            });
            massLabel = $('#ctrl-mass .handle');

            $('input.color').on('touchstart', function( e ){
                e.preventDefault();
                $(this).focus();
            });

            $('#ctrl-color').minicolors({
                'change': function( hex ){
                    var b = self.$ctxMenu.data('body');
                    if ( b ){
                        b.color(hex);
                    }
                    self.emit('modified');
                }
            });

            $('#ctrl-path').hammer().on('touch', function( e ){
                var b = self.$ctxMenu.data('body');
                $(this).toggleClass('on');
                if ( b ){
                    b.path = !!$(this).hasClass('on');
                }
                self.emit('modified');
            });

            self.$ctxMenu.hammer().on('touch', '.ctrl-close', function( e ){
                e.preventDefault();
                self.contextualMenu( null );
            });


            self.world = Physics( { timestep: 2 }, self.initPhysics.bind( self ) );
        }

    }, ['events']);

    return new Mediator();
});
